import styled from "styled-components";

export const CoverContainer = styled.div`
    width: 100%;
    height: 100vh;
    justify-content: center;
    background-color: ${(theme) => themes.bgc};
`;

export const CoverWrapper = styled.div`
    width: 100%;
    height: 100vh;
    margin: auto;
    max-width: 1300px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
`;

export const InfoContainer = styled.div`
    width: 50%;
    margin: auto;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;

    ${'' /* svg {
        font-size: 6rem;
        fill: #393939;
    } */}

    h1 {
        text-align: center;
        width: 100%;
        font-family: "Roboto";
        color: ${(theme) => theme.text};

        &:nth-of-type(1){
            font-weight: 300;
            font-size: 1.5rem;
        }

        &:nth-of-type(2){
            font-weight: 700;
            font-size: 4rem;
        }
    }
`;


export const CustomButton = styled.button`
    margin-top: 100px;
    color: #eee;
    background-color: #393939;
    font-family: "Roboto";
    font-size: 1rem;
    outline: none;
    border: none;
    border-radius: 10px;
    padding: 1rem 2rem;
    cursor: pointer;

    &:hover {
        background-color: #000;
        transition: all .5s ease-in;
    }
`;

